using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        var httpClient = new HttpClient();\n        var message = new { message = "This is an anonymous email." };\n        var jsonContent = new StringContent(Newtonsoft.Json.JsonConvert.SerializeObject(message), Encoding.UTF8, "application/json");\n\n        var response = await httpClient.PostAsync("http://your-backend-service/send-email", jsonContent);\n\n        if (response.IsSuccessStatusCode)\n        {\n            Console.WriteLine("Email sent successfully.");\n        }\n        else\n        {\n            Console.WriteLine($"Error sending email: {response.ReasonPhrase}");\n        }\n    }\n}