protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        TimeSpan startTime = new TimeSpan(8, 0, 0); // 8:00 AM\n        TimeSpan endTime = new TimeSpan(19, 0, 0); // 7:00 PM\n        TimeSpan interval = new TimeSpan(0, 15, 0); // 15 minutes\n\n        for (TimeSpan time = startTime; time < endTime; time = time.Add(interval))\n        {\n            string formattedTime = DateTime.Today.Add(time).ToString("h:mm tt");\n            TimeListBox.Items.Add(new ListItem(formattedTime));\n        }\n    }\n}