using System;\nusing System.Text;\nusing System.Security.Cryptography;\nusing System.Web;\n\npublic static class CookieEncryption\n{\n    private static readonly byte[] key = Encoding.UTF8.GetBytes("your-16-char-key");\n    private static readonly byte[] iv = Encoding.UTF8.GetBytes("your-16-char-iv");\n\n    public static string Encrypt(string plainText)\n    {\n        using (Aes aesAlg = Aes.Create())\n        {\n            aesAlg.Key = key;\n            aesAlg.IV = iv;\n\n            ICryptoTransform encryptor = aesAlg.CreateEncryptor(aesAlg.Key, aesAlg.IV);\n\n            using (var msEncrypt = new System.IO.MemoryStream())\n            {\n                using (var csEncrypt = new CryptoStream(msEncrypt, encryptor, CryptoStreamMode.Write))\n                {\n                    using (var swEncrypt = new System.IO.StreamWriter(csEncrypt))\n                    {\n                        swEncrypt.Write(plainText);\n                    }\n                    return Convert.ToBase64String(msEncrypt.ToArray());\n                }\n            }\n        }\n    }\n\n    public static string Decrypt(string cipherText)\n    {\n        using (Aes aesAlg = Aes.Create())\n        {\n            aesAlg.Key = key;\n            aesAlg.IV = iv;\n\n            ICryptoTransform decryptor = aesAlg.CreateDecryptor(aesAlg.Key, aesAlg.IV);\n\n            using (var msDecrypt = new System.IO.MemoryStream(Convert.FromBase64String(cipherText)))\n            {\n                using (var csDecrypt = new CryptoStream(msDecrypt, decryptor, CryptoStreamMode.Read))\n                {\n                    using (var srDecrypt = new System.IO.StreamReader(csDecrypt))\n                    {\n                        return srDecrypt.ReadToEnd();\n                    }\n                }\n            }\n        }\n    }\n}\n\n// Usage example\nstring originalValue = "SensitiveCookieData";\nstring encryptedValue = CookieEncryption.Encrypt(originalValue);\nHttpCookie cookie = new HttpCookie("MyCookie", encryptedValue);\nHttpContext.Current.Response.Cookies.Add(cookie);