using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseTimeoutTest\n{\n    public void TestTimeout()\n    {\n        string connectionString = "your_connection_string_here";\n        string query = "WAITFOR DELAY '00:01:00';"; // 1-minute delay\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand(query, connection);\n            command.CommandTimeout = 30; // Set timeout to 30 seconds\n\n            try\n            {\n                connection.Open();\n                command.ExecuteNonQuery();\n            }\n            catch (SqlException ex)\n            {\n                if (ex.Number == -2) // -2 is the error number for timeout.\n                {\n                    Console.WriteLine("Timeout occurred as expected.");\n                }\n                else\n                {\n                    Console.WriteLine("An unexpected error occurred: " + ex.Message);\n                }\n            }\n        }\n    }\n}