using iText.Kernel.Pdf;\nusing iText.Layout;\nusing iText.Layout.Element;\n\npublic class PdfEditor\n{\n    public void EditPdf(string src, string dest)\n    {\n        PdfDocument pdfDoc = new PdfDocument(new PdfReader(src), new PdfWriter(dest));\n        Document document = new Document(pdfDoc);\n\n        // Add a paragraph to the existing PDF\n        document.Add(new Paragraph("This is an edited PDF document."));\n\n        document.Close();\n    }\n}\n\n// Usage\nPdfEditor editor = new PdfEditor();\neditor.EditPdf("path/to/source.pdf", "path/to/edited.pdf");