using System;\n   using System.Management;\n\n   namespace ProcessMonitor\n   {\n       class Program\n       {\n           static void Main(string[] args)\n           {\n               ManagementEventWatcher startWatch = new ManagementEventWatcher(\n                   new WqlEventQuery("SELECT * FROM Win32_ProcessStartTrace"));\n\n               startWatch.EventArrived += new EventArrivedEventHandler(ProcessStarted);\n               startWatch.Start();\n\n               Console.WriteLine("Monitoring process starts. Press any key to exit.");\n               Console.ReadKey();\n               startWatch.Stop();\n           }\n\n           private static void ProcessStarted(object sender, EventArrivedEventArgs e)\n           {\n               Console.WriteLine($"Process started: {e.NewEvent.Properties["ProcessName"].Value}");\n               // Add logic to block the process if necessary\n           }\n       }\n   }