using System;\nusing Microsoft.Build.Evaluation;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new MSBuild project\n        Project project = new Project();\n\n        // Set some basic properties\n        project.SetProperty("OutputType", "Exe");\n        project.SetProperty("TargetFrameworkVersion", "v3.5");\n        project.SetProperty("Configuration", "Debug");\n        project.SetProperty("Platform", "AnyCPU");\n\n        // Add an item (e.g., a source file)\n        project.AddItem("Compile", "Program.cs");\n\n        // Add an import (e.g., the common targets file)\n        project.Xml.AddImport("$(MSBuildToolsPath)\\Microsoft.CSharp.targets");\n\n        // Save the project to a file\n        project.Save("MyProject.csproj");\n\n        Console.WriteLine("Project file created successfully.");\n    }\n}