using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Windows.Forms;\n\npublic class MultiColumnListBox : ListBox\n{\n    public MultiColumnListBox()\n    {\n        this.DrawMode = DrawMode.OwnerDrawVariable;\n        this.MeasureItem += new MeasureItemEventHandler(OnMeasureItem);\n        this.DrawItem += new DrawItemEventHandler(OnDrawItem);\n    }\n\n    protected void OnMeasureItem(object sender, MeasureItemEventArgs e)\n    {\n        e.ItemHeight = 20;\n    }\n\n    protected void OnDrawItem(object sender, DrawItemEventArgs e)\n    {\n        if (e.Index < 0)\n            return;\n\n        e.DrawBackground();\n        var item = (Tuple<string, string>)this.Items[e.Index];\n        e.Graphics.DrawString(item.Item1, e.Font, SystemBrushes.ControlText, e.Bounds.Left, e.Bounds.Top);\n        e.Graphics.DrawString(item.Item2, e.Font, SystemBrushes.ControlText, e.Bounds.Left + 100, e.Bounds.Top);\n        e.DrawFocusRectangle();\n    }\n\n    public void BindData<T>(List<T> data, Func<T, Tuple<string, string>> formatFunc)\n    {\n        this.Items.Clear();\n        foreach (var item in data)\n        {\n            this.Items.Add(formatFunc(item));\n        }\n    }\n}\n\npublic class ExampleForm : Form\n{\n    public ExampleForm()\n    {\n        var listBox = new MultiColumnListBox { Dock = DockStyle.Fill };\n        this.Controls.Add(listBox);\n\n        var data = new List<MyData>\n        {\n            new MyData { Column1 = "Item1Col1", Column2 = "Item1Col2" },\n            new MyData { Column1 = "Item2Col1", Column2 = "Item2Col2" }\n        };\n\n        listBox.BindData(data, item => Tuple.Create(item.Column1, item.Column2));\n    }\n\n    public class MyData\n    {\n        public string Column1 { get; set; }\n        public string Column2 { get; set; }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new ExampleForm());\n    }\n}