public class ProxyService : IProxyService\n   {\n       public string GetData(int value)\n       {\n           // Create a channel to the real service\n           var binding = new BasicHttpBinding();\n           var endpoint = new EndpointAddress("http://realserviceaddress/RealService.svc");\n           var channelFactory = new ChannelFactory<IProxyService>(binding, endpoint);\n           var realService = channelFactory.CreateChannel();\n\n           // Forward the call to the real service\n           return realService.GetData(value);\n       }\n   }