using System;\nusing System.DirectoryServices.AccountManagement;\n\npublic class LdapAuthentication\n{\n    public static bool Authenticate(string ldapServer, string domain, string username, string password)\n    {\n        bool isAuthenticated = false;\n\n        try\n        {\n            using (PrincipalContext pc = new PrincipalContext(ContextType.Domain, ldapServer, domain, username, password))\n            {\n                isAuthenticated = pc.ValidateCredentials(username, password);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Exception: " + ex.Message);\n        }\n\n        return isAuthenticated;\n    }\n\n    public static void Main(string[] args)\n    {\n        string ldapServer = "testdomain.com";\n        string domain = "testdomain";\n        string username = "yourUsername";\n        string password = "yourPassword";\n\n        bool isAuthenticated = Authenticate(ldapServer, domain, username, password);\n\n        Console.WriteLine("Authenticated: " + isAuthenticated);\n    }\n}