using System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\nusing System.IO;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example data\n        var words = new[]\n        {\n            new { English = "bait", Punjabi = "ਦਾਣਾ" },\n            new { English = "baked", Punjabi = "ਭੁੰਨਿਆ" },\n            new { English = "balance", Punjabi = "ਵਿਚਾਰ" }\n        };\n\n        // Directory to save images\n        string imagesDir = "images";\n        Directory.CreateDirectory(imagesDir);\n\n        // HTML string builder\n        StringBuilder html = new StringBuilder();\n        html.Append("<html><body><table>");\n\n        foreach (var word in words)\n        {\n            string imagePath = Path.Combine(imagesDir, word.Punjabi + ".png");\n            CreateImageFromText(word.Punjabi, imagePath);\n\n            html.Append("<tr>");\n            html.AppendFormat("<td>{0}</td>", word.English);\n            html.AppendFormat("<td><img src='{0}' alt='{1}' /></td>", imagePath, word.Punjabi);\n            html.Append("</tr>");\n        }\n\n        html.Append("</table></body></html>");\n\n        // Save HTML file\n        File.WriteAllText("output.html", html.ToString());\n    }\n\n    static void CreateImageFromText(string text, string outputPath)\n    {\n        Font font = new Font("Arial", 20, FontStyle.Regular, GraphicsUnit.Pixel);\n        SizeF textSize;\n\n        using (Bitmap tempImg = new Bitmap(1, 1))\n        {\n            using (Graphics tempG = Graphics.FromImage(tempImg))\n            {\n                textSize = tempG.MeasureString(text, font);\n            }\n        }\n\n        using (Bitmap img = new Bitmap((int)textSize.Width, (int)textSize.Height))\n        {\n            using (Graphics g = Graphics.FromImage(img))\n            {\n                g.Clear(Color.White);\n                using (Brush brush = new SolidBrush(Color.Black))\n                {\n                    g.DrawString(text, font, brush, 0, 0);\n                }\n                img.Save(outputPath, ImageFormat.Png);\n            }\n        }\n    }\n}