using System;\nusing System.ComponentModel.Composition.Hosting;\nusing System.Linq;\nusing System.Reflection;\nusing System.Collections.Generic;\n\n// Assuming you have an AggregateCatalog or DirectoryCatalog\nAggregateCatalog catalog = new AggregateCatalog();\ncatalog.Catalogs.Add(new DirectoryCatalog("PathToYourAssemblies"));\n\nIEnumerable<Assembly> assemblies = catalog.Parts\n    .Select(part => part.GetType().Assembly)\n    .Distinct();\n\nforeach (var assembly in assemblies)\n{\n    Console.WriteLine($"Assembly: {assembly.FullName}");\n    Console.WriteLine($"Version: {assembly.GetName().Version}");\n    Console.WriteLine($"Copyright: {assembly.GetCustomAttribute<AssemblyCopyrightAttribute>()?.Copyright}");\n    // Add more attributes as needed\n}