public class HealthCheckController : Controller\n   {\n       [HttpGet]\n       [Route("health")]\n       public IActionResult GetHealth()\n       {\n           // Perform health checks\n           bool databaseHealth = CheckDatabaseHealth();\n           bool serviceHealth = CheckServiceHealth();\n\n           if (databaseHealth && serviceHealth)\n           {\n               return Ok("Healthy");\n           }\n           return StatusCode(500, "Unhealthy");\n       }\n   }