using System;\nusing Microsoft.SharePoint.Client;\n\nnamespace HideSharePointLists\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string siteUrl = "https://yoursharepointsiteurl";\n            string[] listsToHide = { "List1", "List2", "List3" };\n\n            ClientContext context = new ClientContext(siteUrl);\n            Web web = context.Web;\n\n            foreach (var listName in listsToHide)\n            {\n                List list = web.Lists.GetByTitle(listName);\n                context.Load(list);\n                context.ExecuteQuery();\n\n                if (list != null)\n                {\n                    list.Hidden = true;\n                    list.Update();\n                    context.ExecuteQuery();\n                    Console.WriteLine($"{listName} has been hidden.");\n                }\n            }\n        }\n    }\n}