using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string htmlSourceString = "your_html_string_here";\n        string pattern = "href=[\\\"\\\'](http:\\/\\/|\\.\\/|\\/)?\\w+(\\.\\w+)*(\\/\\w+(\\.\\w+)?)*(\\/|\\?\\w*=\\w*(&\\w*=\\w*)*)?[\\\"\\\']";\n\n        MatchCollection matches = Regex.Matches(htmlSourceString, pattern);\n\n        foreach (Match match in matches)\n        {\n            if (match.Success)\n            {\n                Console.WriteLine("value = " + match.Value);\n            }\n        }\n    }\n}