using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing System.Windows.Forms;\n\nnamespace WebsiteMonitor\n{\n    public partial class MainForm : Form\n    {\n        private static readonly HttpClient client = new HttpClient();\n        private readonly string url = "https://yourwebsite.com"; // Replace with your website URL\n        private readonly Timer timer;\n\n        public MainForm()\n        {\n            InitializeComponent();\n\n            timer = new Timer();\n            timer.Interval = 60000; // Set the interval to 60 seconds (60000 milliseconds)\n            timer.Tick += Timer_Tick;\n            timer.Start();\n        }\n\n        private async void Timer_Tick(object sender, EventArgs e)\n        {\n            await CheckWebsiteStatus();\n        }\n\n        private async Task CheckWebsiteStatus()\n        {\n            try\n            {\n                HttpResponseMessage response = await client.GetAsync(url);\n                if (!response.IsSuccessStatusCode)\n                {\n                    ShowErrorMessage($"Error: {response.StatusCode}");\n                }\n            }\n            catch (HttpRequestException ex)\n            {\n                ShowErrorMessage($"Network error: {ex.Message}");\n            }\n        }\n\n        private void ShowErrorMessage(string message)\n        {\n            MessageBox.Show(message, "Website Status", MessageBoxButtons.OK, MessageBoxIcon.Error);\n        }\n    }\n}