public class RateLimiter\n   {\n       private static Dictionary<string, DateTime> requestTracker = new Dictionary<string, DateTime>();\n\n       public bool IsRequestAllowed(string clientId)\n       {\n           if(requestTracker.ContainsKey(clientId) && requestTracker[clientId] > DateTime.Now.AddSeconds(-1))\n           {\n               return false;\n           }\n           requestTracker[clientId] = DateTime.Now;\n           return true;\n       }\n   }