using System.Web.Mvc;\nusing System.Threading.Tasks;\n\npublic class JobController : Controller\n{\n    // Store job status in a static variable for simplicity\n    // In production, consider using persistent storage like a database\n    private static string JobStatus = "Pending";\n\n    [HttpPost]\n    public ActionResult StartJob()\n    {\n        // Start the long-running job asynchronously\n        Task.Run(() => {\n            JobStatus = "Working";\n            // Simulate long-running job\n            System.Threading.Thread.Sleep(5000);\n            JobStatus = "Done";\n        });\n\n        return Json(new { success = true });\n    }\n\n    [HttpGet]\n    public ActionResult GetJobStatus()\n    {\n        return Json(new { status = JobStatus }, JsonRequestBehavior.AllowGet);\n    }\n}