using System.Web.Security;\n\npublic void ConfigureRoleProvider()\n{\n    SqlRoleProvider roleProvider = new SqlRoleProvider();\n    roleProvider.Initialize("SqlRoleProvider", new System.Collections.Specialized.NameValueCollection\n    {\n        { "connectionStringName", "YourConnectionString" },\n        { "applicationName", "/" }\n    });\n\n    Roles.Provider = roleProvider;\n}