using System;\nusing System.Management;\n\npublic class RemoteProcessStarter\n{\n    public static void StartProcess(string machineName, string processPath)\n    {\n        ConnectionOptions options = new ConnectionOptions();\n        options.Impersonation = ImpersonationLevel.Impersonate;\n        options.EnablePrivileges = true;\n\n        ManagementScope scope = new ManagementScope($@"\\{machineName}\root\cimv2", options);\n        scope.Connect();\n\n        ManagementClass processClass = new ManagementClass(scope, new ManagementPath("Win32_Process"), new ObjectGetOptions());\n        ManagementBaseObject inParams = processClass.GetMethodParameters("Create");\n        inParams["CommandLine"] = processPath;\n\n        ManagementBaseObject outParams = processClass.InvokeMethod("Create", inParams, null);\n        Console.WriteLine("Process ID: " + outParams["ProcessId"]);\n    }\n}