using System;\nusing System.Collections.Specialized;\n\nclass Program\n{\n    static void Main()\n    {\n        OrderedDictionary orderedDict = new OrderedDictionary();\n\n        // Adding items to the OrderedDictionary\n        orderedDict.Add("key1", "value1");\n        orderedDict.Add("key2", "value2");\n        orderedDict.Add("key3", "value3");\n\n        // Enumerating items in addition order\n        foreach (DictionaryEntry entry in orderedDict)\n        {\n            Console.WriteLine($"Key: {entry.Key}, Value: {entry.Value}");\n        }\n\n        // Retrieving items by key\n        Console.WriteLine($"Retrieved by key 'key2': {orderedDict["key2"]}");\n    }\n}