using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseCleaner\n{\n    private readonly string connectionString;\n\n    public DatabaseCleaner(string connectionString)\n    {\n        this.connectionString = connectionString;\n    }\n\n    public void CleanTables()\n    {\n        // Make sure to use connection pooling properly\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            SqlCommand command = new SqlCommand("DELETE FROM YourTable WHERE Condition = 'Value'", connection);\n            command.ExecuteNonQuery();\n        }\n    }\n}\n\n// Usage\nstring connectionString = "your_connection_string;Pooling=true;Min Pool Size=5;Max Pool Size=100;";\nDatabaseCleaner cleaner = new DatabaseCleaner(connectionString);\ncleaner.CleanTables();