using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class MySqlPasswordHasher\n{\n    public static string MySqlPassword(string password)\n    {\n        // Step 1: SHA1 hash of the password\n        using (SHA1 sha1 = SHA1.Create())\n        {\n            byte[] sha1Bytes = sha1.ComputeHash(Encoding.UTF8.GetBytes(password));\n            string sha1Hex = BitConverter.ToString(sha1Bytes).Replace("-", "").ToLower();\n\n            // Step 2: SHA1 hash of the previous SHA1 hash\n            byte[] doubleSha1Bytes = sha1.ComputeHash(Encoding.UTF8.GetBytes(sha1Hex));\n            StringBuilder hashedPassword = new StringBuilder("*");\n            \n            // Step 3: Convert to hexadecimal string\n            foreach (byte bytePart in doubleSha1Bytes)\n            {\n                hashedPassword.Append(bytePart.ToString("x2"));\n            }\n\n            return hashedPassword.ToString();\n        }\n    }\n\n    public static void Main()\n    {\n        string password = "your_password";\n        string hashedPassword = MySqlPassword(password);\n        Console.WriteLine(hashedPassword);\n    }\n}