using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseOperations\n{\n    private string connectionString = "YourConnectionString";\n\n    public void ExecuteQuery(string tableName, string columnName)\n    {\n        // Validate the table and column names if necessary\n        // Example: Ensure tableName and columnName are from a predefined list\n\n        // Construct the SQL query dynamically\n        string query = $"SELECT {columnName} FROM {tableName} WHERE YourCondition";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand(query, connection);\n\n            try\n            {\n                connection.Open();\n                SqlDataReader reader = command.ExecuteReader();\n                while (reader.Read())\n                {\n                    Console.WriteLine(String.Format("{0}", reader[0]));\n                }\n                reader.Close();\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine(ex.Message);\n            }\n        }\n    }\n}