using System.Web.Security;\n\npublic void RenameRole(string oldRoleName, string newRoleName)\n{\n    if (!Roles.RoleExists(oldRoleName))\n    {\n        throw new ArgumentException("The old role does not exist.", nameof(oldRoleName));\n    }\n\n    if (Roles.RoleExists(newRoleName))\n    {\n        throw new ArgumentException("The new role already exists.", nameof(newRoleName));\n    }\n\n    // Create the new role\n    Roles.CreateRole(newRoleName);\n\n    // Get users in the old role\n    string[] usersInOldRole = Roles.GetUsersInRole(oldRoleName);\n\n    // Add users to the new role\n    foreach (string user in usersInOldRole)\n    {\n        Roles.AddUserToRole(user, newRoleName);\n    }\n\n    // Remove users from the old role\n    foreach (string user in usersInOldRole)\n    {\n        Roles.RemoveUserFromRole(user, oldRoleName);\n    }\n\n    // Delete the old role\n    Roles.DeleteRole(oldRoleName);\n}