using System.Net;\nusing System.Net.Mail;\n\nMailMessage message = new MailMessage();\nmessage.From = new MailAddress("from@example.com");\nmessage.To.Add("to@domain.com");\nmessage.Subject = "test";\nmessage.Body = "This is a test email.";\n\nSmtpClient smtp = new SmtpClient(ConfigurationUtil.SMTPServer);\nsmtp.Port = 587; // Use 465 if your server requires SSL instead of TLS\nsmtp.EnableSsl = true; // Enable SSL/TLS\nsmtp.Credentials = new NetworkCredential("from@example.com", "password");\n\ntry\n{\n    smtp.Send(message);\n    Console.WriteLine("Email sent successfully.");\n}\ncatch (Exception ex)\n{\n    Console.WriteLine("Failed to send email: " + ex.Message);\n}