using System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\nusing System.IO;\n\npublic class ImageConverter\n{\n    public static byte[] ConvertBitmapToJpeg(byte[] bitmapBytes)\n    {\n        using (MemoryStream bitmapStream = new MemoryStream(bitmapBytes))\n        {\n            // Load the Bitmap from the byte array\n            using (Bitmap bitmap = new Bitmap(bitmapStream))\n            {\n                using (MemoryStream jpegStream = new MemoryStream())\n                {\n                    // Save the Bitmap as a JPEG to the memory stream\n                    bitmap.Save(jpegStream, ImageFormat.Jpeg);\n                    \n                    // Return the JPEG byte array\n                    return jpegStream.ToArray();\n                }\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        // Example usage\n        byte[] bitmapBytes = File.ReadAllBytes("path_to_bitmap_file.bmp");\n        byte[] jpegBytes = ConvertBitmapToJpeg(bitmapBytes);\n        \n        // Save the converted JPEG to a file\n        File.WriteAllBytes("output_file.jpg", jpegBytes);\n    }\n}