using HtmlAgilityPack;\n\nclass Program\n{\n    static void Main()\n    {\n        string html = "<html><body><p>Hello, World!</p></body></html>";\n\n        // Load the HTML string into an HtmlDocument\n        HtmlDocument document = new HtmlDocument();\n        document.LoadHtml(html);\n\n        // Manipulate the DOM\n        HtmlNode bodyNode = document.DocumentNode.SelectSingleNode("//body");\n        HtmlNode newParagraph = HtmlNode.CreateNode("<p>This is a new paragraph.</p>");\n        bodyNode.AppendChild(newParagraph);\n\n        // Output the modified HTML\n        string modifiedHtml = document.DocumentNode.OuterHtml;\n        System.Console.WriteLine(modifiedHtml);\n    }\n}