private void DoA(object param)\n   {\n       if (param == null)\n       {\n           throw new ArgumentNullException(nameof(param), "Parameter cannot be null");\n       }\n\n       DoD(param);\n   }\n\n   private void DoD(object param)\n   {\n       if (param == null)\n       {\n           throw new ArgumentNullException(nameof(param), "Parameter cannot be null");\n       }\n\n       // Error if param == null\n       param.DoX();\n   }