using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Record\n{\n    public string Username { get; set; }\n    public DateTime Date { get; set; }\n    public DateTime LoginTime { get; set; }\n    public int Amount { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        var records = new List<Record>\n        {\n            new Record { Username = "A", Date = new DateTime(2023, 10, 1), LoginTime = new DateTime(2023, 10, 1, 1, 0, 0), Amount = 50 },\n            new Record { Username = "A", Date = new DateTime(2023, 10, 1), LoginTime = new DateTime(2023, 10, 1, 2, 0, 0), Amount = 50 },\n            new Record { Username = "A", Date = new DateTime(2023, 10, 2), LoginTime = new DateTime(2023, 10, 2, 1, 0, 0), Amount = 20 },\n            new Record { Username = "A", Date = new DateTime(2023, 10, 2), LoginTime = new DateTime(2023, 10, 2, 2, 0, 0), Amount = 60 },\n            new Record { Username = "B", Date = new DateTime(2023, 10, 1), LoginTime = new DateTime(2023, 10, 1, 1, 0, 0), Amount = 35 },\n            new Record { Username = "B", Date = new DateTime(2023, 10, 1), LoginTime = new DateTime(2023, 10, 1, 2, 0, 0), Amount = 35 },\n            new Record { Username = "C", Date = new DateTime(2023, 10, 1), LoginTime = new DateTime(2023, 10, 1, 1, 0, 0), Amount = 80 },\n            new Record { Username = "D", Date = new DateTime(2023, 10, 1), LoginTime = new DateTime(2023, 10, 1, 1, 0, 0), Amount = 12 },\n            new Record { Username = "D", Date = new DateTime(2023, 10, 1), LoginTime = new DateTime(2023, 10, 1, 2, 0, 0), Amount = 12 },\n            new Record { Username = "D", Date = new DateTime(2023, 10, 1), LoginTime = new DateTime(2023, 10, 1, 3, 0, 0), Amount = 15 },\n            new Record { Username = "D", Date = new DateTime(2023, 10, 2), LoginTime = new DateTime(2023, 10, 2, 1, 0, 0), Amount = 12 }\n        };\n\n        var updatedRecords = records\n            .GroupBy(r => new { r.Username, r.Date })\n            .SelectMany(g =>\n            {\n                var maxLoginTime = g.Max(r => r.LoginTime);\n                return g.Select(r => \n                {\n                    if (r.LoginTime != maxLoginTime)\n                    {\n                        r.Amount = 0;\n                    }\n                    return r;\n                });\n            })\n            .ToList();\n\n        foreach (var record in updatedRecords)\n        {\n            Console.WriteLine($"{record.Username} {record.Date.ToShortDateString()} {record.LoginTime.ToShortTimeString()} {record.Amount}");\n        }\n    }\n}