using System;\nusing System.Data;\nusing System.Windows.Forms;\n\npublic partial class Form1 : Form\n{\n    public Form1()\n    {\n        InitializeComponent();\n        \n        // Create a DataTable and define its structure\n        DataTable table = new DataTable();\n        table.Columns.Add("ColA", typeof(int));\n        table.Columns.Add("ColB", typeof(int));\n        table.Columns.Add("ColC", typeof(string));\n\n        // Define the primary key (compound key) of the DataTable\n        table.PrimaryKey = new DataColumn[] { table.Columns["ColA"], table.Columns["ColB"] };\n\n        // Add some rows to the DataTable\n        table.Rows.Add(1, 1, "Value1");\n        table.Rows.Add(6, 5, "Value2");\n        table.Rows.Add(3, 4, "Value3");\n\n        // Find the row with the compound key (6, 5)\n        DataRow foundRow = table.Rows.Find(new object[] { 6, 5 });\n\n        if (foundRow != null)\n        {\n            MessageBox.Show("Row found: ColC = " + foundRow["ColC"]);\n        }\n        else\n        {\n            MessageBox.Show("Row not found");\n        }\n    }\n}