using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        XDocument doc = XDocument.Load("file.xml");\n        string idToFind = "ID1";\n        var dest = doc.Descendants().SingleOrDefault(e => (string)e.Attribute("id") == idToFind);\n\n        if (dest != null)\n        {\n            Console.WriteLine("Element found: " + dest);\n        }\n        else\n        {\n            Console.WriteLine("Element not found.");\n        }\n    }\n}