using System;\nusing System.Management;\n\nnamespace RegistryWatcher\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string registryPath = @"HKEY_LOCAL_MACHINE\SOFTWARE\YourSoftware";\n            string valueName = "YourValue";\n\n            ManagementEventWatcher watcher = new ManagementEventWatcher(\n                new WqlEventQuery(\n                    "SELECT * FROM RegistryValueChangeEvent WHERE Hive = 'HKEY_LOCAL_MACHINE' " +\n                    $"AND KeyPath = '{registryPath.Replace(@"\", @"\\")}' AND ValueName = '{valueName}'"));\n\n            watcher.EventArrived += new EventArrivedEventHandler(OnRegistryValueChange);\n            watcher.Start();\n\n            Console.WriteLine("Watching for registry value changes. Press any key to exit.");\n            Console.ReadKey();\n\n            watcher.Stop();\n        }\n\n        private static void OnRegistryValueChange(object sender, EventArrivedEventArgs e)\n        {\n            Console.WriteLine("Registry value changed!");\n            // Add your logic here to handle the change event\n        }\n    }\n}