using System;\nusing System.DirectoryServices.AccountManagement;\n\npublic class GroupCheck\n{\n    public static bool IsUserInGroup(string username, string groupName)\n    {\n        using (PrincipalContext context = new PrincipalContext(ContextType.Domain))\n        {\n            UserPrincipal user = UserPrincipal.FindByIdentity(context, username);\n            GroupPrincipal group = GroupPrincipal.FindByIdentity(context, groupName);\n\n            if (user != null && group != null)\n            {\n                return user.IsMemberOf(group);\n            }\n            else\n            {\n                return false;\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string username = "johndoe";\n        string groupName = "Domain Users"; // Use the display name or true group name\n\n        bool isInGroup = IsUserInGroup(username, groupName);\n        Console.WriteLine($"{username} is in group {groupName}: {isInGroup}");\n    }\n}