using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        DataSet dataSet = new DataSet();\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            // Retrieve the list of tables\n            DataTable tables = connection.GetSchema("Tables");\n\n            foreach (DataRow row in tables.Rows)\n            {\n                string tableName = row["TABLE_NAME"].ToString();\n\n                // Create a new SqlDataAdapter for each table\n                string query = $"SELECT * FROM {tableName}";\n                SqlDataAdapter adapter = new SqlDataAdapter(query, connection);\n\n                // Fill the DataSet with the data from the current table\n                adapter.Fill(dataSet, tableName);\n            }\n        }\n\n        // Now the DataSet contains all the data from all tables\n        // Example: Print the data from each table\n        foreach (DataTable table in dataSet.Tables)\n        {\n            Console.WriteLine($"Table: {table.TableName}");\n            foreach (DataRow row in table.Rows)\n            {\n                foreach (DataColumn column in table.Columns)\n                {\n                    Console.Write($"{column.ColumnName}: {row[column]} ");\n                }\n                Console.WriteLine();\n            }\n            Console.WriteLine();\n        }\n    }\n}