using System;\nusing System.ServiceModel;\n\npublic class DynamicSoapClient\n{\n    public void SendData(string serviceUrl, string xmlData)\n    {\n        // Assuming you have a generated client proxy class named 'MyServiceClient'\n        var binding = new BasicHttpBinding();\n        var endpoint = new EndpointAddress(serviceUrl);\n\n        var client = new MyServiceClient(binding, endpoint);\n        \n        // Assuming your service has a method named 'UpdateData'\n        client.UpdateData(xmlData);\n\n        client.Close();\n    }\n}