public Color x, y;\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    BackGroundColorArranger(Color.Red, Color.Salmon);\n}\n\nvoid BackGroundColorArranger(Color x, Color y)\n{\n    int avgColorValue = Convert.ToInt32((float)(x.ToArgb() + y.ToArgb()) / 2);\n    BackGroundColorArrangerBase(avgColorValue);\n}\n\nvoid BackGroundColorArrangerBase(int z)\n{\n    Panel panel = new Panel\n    {\n        ID = z.ToString(),\n        Width = 150,\n        Height = 50,\n        BackColor = Color.FromArgb(z)\n    };\n    \n    this.Controls.Add(panel);\n    // Remove or comment out the recursive call to prevent stack overflow\n    // BackGroundColorArranger(x, Color.FromArgb(z));\n}