// Subscribe to the CellChange event\nultraGrid1.CellChange += new Infragistics.Win.UltraWinGrid.CellEventHandler(UltraGrid1_CellChange);\n\n// Event handler for CellChange event\nprivate void UltraGrid1_CellChange(object sender, Infragistics.Win.UltraWinGrid.CellEventArgs e)\n{\n    // Get the cell that has changed\n    var changedCell = e.Cell;\n\n    // Do something with the changed cell\n    Console.WriteLine($"Cell at row {changedCell.Row.Index}, column {changedCell.Column.Key} changed to {changedCell.Text}");\n}