using System;\nusing System.Collections.Generic;\n\npublic class Customer\n{\n    private Guid _id;\n    private string _name;\n    private ISet<CustomerPreferences> _customerPreferences = new HashSet<CustomerPreferences>();\n    private IList<User> _users = new List<User>();\n\n    public Guid Id\n    {\n        get { return _id; }\n        set { _id = value; }\n    }\n\n    public string Name\n    {\n        get { return _name; }\n        set { _name = value; }\n    }\n\n    public ISet<CustomerPreferences> CustomerPreferences\n    {\n        get { return _customerPreferences; }\n        set { _customerPreferences = value; }\n    }\n\n    public IList<User> Users\n    {\n        get { return _users; }\n        set { _users = value; }\n    }\n}