using System.Collections.Generic;\nusing System.Data;\n\npublic class PersonForm\n{\n    private DataSet _personInfo;\n    private List<string> _whatChanged = new List<string>();\n\n    public void LoadPersonInfo(int personID)\n    {\n        _personInfo = ConnectBLL.BLL.Person.GetPerson(personID);\n        using (var tmpPersonDT = _personInfo.Tables[0])\n        {\n            if (tmpPersonDT.Rows.Count > 0)\n            {\n                foreach (DataRow row in tmpPersonDT.Rows)\n                {\n                    CheckPersonData(row);\n                }\n            }\n        }\n    }\n\n    private void CheckPersonData(DataRow row)\n    {\n        CompareAndSet(row, "ResidencyCountyID", lkuResidenceCounty.EditValue, "ResidencyCounty");\n        CompareAndSet(row, "ResponsibilityCountyID", lkuResponsibleCounty.EditValue, "ResponsibilityCounty");\n        CompareAndSet(row, "HispanicOriginFlag", chkHispanic.EditValue, "HispanicOriginFlag");\n        CompareAndSet(row, "CitizenFlag", chkCitizen.EditValue, "CitizenFlag");\n        CompareAndSet(row, "VeteranFlag", chkVeteran.EditValue, "VeteranFlag");\n\n        // Add more comparisons as needed\n    }\n\n    private void CompareAndSet(DataRow row, string columnName, object controlValue, string changeDescription)\n    {\n        if (!object.Equals(row[columnName], controlValue))\n        {\n            row[columnName] = controlValue;\n            _whatChanged.Add(changeDescription);\n        }\n    }\n}