using HtmlAgilityPack;\nusing System;\nusing System.Collections.Generic;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string html = @"\n        <html>\n            <body>\n                <img src='image1.jpg' alt='Image 1' />\n                <img src='image2.png' alt='Image 2' />\n                <img src='image3.gif' alt='Image 3' />\n            </body>\n        </html>";\n\n        List<string> imageSrcs = GetImageSourcesFromHtml(html);\n        foreach (string src in imageSrcs)\n        {\n            Console.WriteLine(src);  // Output: image1.jpg, image2.png, image3.gif\n        }\n    }\n\n    public static List<string> GetImageSourcesFromHtml(string html)\n    {\n        var imageSrcs = new List<string>();\n        var htmlDoc = new HtmlDocument();\n        htmlDoc.LoadHtml(html);\n\n        var imgNodes = htmlDoc.DocumentNode.SelectNodes("//img");\n        if (imgNodes != null)\n        {\n            foreach (var imgNode in imgNodes)\n            {\n                var src = imgNode.GetAttributeValue("src", null);\n                if (!string.IsNullOrEmpty(src))\n                {\n                    imageSrcs.Add(src);\n                }\n            }\n        }\n\n        return imageSrcs;\n    }\n}