// Example of a simplified data access layer using Entity Framework\npublic class ClientCampaignRepository\n{\n    private readonly MyDbContext _context;\n\n    public ClientCampaignRepository(MyDbContext context)\n    {\n        _context = context;\n    }\n\n    public ClientCampaign GetClientCampaign(int id)\n    {\n        return _context.ClientCampaigns.Find(id);\n    }\n\n    public void AddClientCampaign(ClientCampaign campaign)\n    {\n        _context.ClientCampaigns.Add(campaign);\n        _context.SaveChanges();\n    }\n\n    public void UpdateClientCampaign(ClientCampaign campaign)\n    {\n        _context.ClientCampaigns.Update(campaign);\n        _context.SaveChanges();\n    }\n\n    public void DeleteClientCampaign(int id)\n    {\n        var campaign = _context.ClientCampaigns.Find(id);\n        if (campaign != null)\n        {\n            _context.ClientCampaigns.Remove(campaign);\n            _context.SaveChanges();\n        }\n    }\n}\n\n// Simplified Web Service using the repository\n[WebService]\npublic class CampaignService : System.Web.Services.WebService\n{\n    private readonly ClientCampaignRepository _repository;\n\n    public CampaignService()\n    {\n        _repository = new ClientCampaignRepository(new MyDbContext());\n    }\n\n    [WebMethod]\n    public ClientCampaign GetClientCampaign(int id)\n    {\n        return _repository.GetClientCampaign(id);\n    }\n\n    [WebMethod]\n    public void AddClientCampaign(ClientCampaign campaign)\n    {\n        _repository.AddClientCampaign(campaign);\n    }\n\n    [WebMethod]\n    public void UpdateClientCampaign(ClientCampaign campaign)\n    {\n        _repository.UpdateClientCampaign(campaign);\n    }\n\n    [WebMethod]\n    public void DeleteClientCampaign(int id)\n    {\n        _repository.DeleteClientCampaign(id);\n    }\n}