using System;\nusing System.IO;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create("https://example.com");\n        request.Method = "POST";\n        request.ContentType = "application/json";\n        string postData = "{\"key\":\"value\"}";\n        using (StreamWriter streamWriter = new StreamWriter(request.GetRequestStream()))\n        {\n            streamWriter.Write(postData);\n        }\n\n        PrintHttpWebRequest(request);\n    }\n\n    static void PrintHttpWebRequest(HttpWebRequest request)\n    {\n        Console.WriteLine("Request Method: " + request.Method);\n        Console.WriteLine("Request URI: " + request.RequestUri);\n        Console.WriteLine("Request Headers:");\n        foreach (string header in request.Headers)\n        {\n            Console.WriteLine(header + ": " + request.Headers[header]);\n        }\n\n        if (request.Method == "POST" || request.Method == "PUT")\n        {\n            using (Stream requestStream = request.GetRequestStream())\n            {\n                requestStream.Position = 0;\n                using (StreamReader reader = new StreamReader(requestStream))\n                {\n                    Console.WriteLine("Request Body: " + reader.ReadToEnd());\n                }\n            }\n        }\n    }\n}