using System;\nusing System.Data;\nusing System.Data.SqlServerCe;\n\npublic class DataSetExample\n{\n    public static void Main()\n    {\n        string connectionString = "Data Source=MyDatabase.sdf;Persist Security Info=False;";\n        using (SqlCeConnection connection = new SqlCeConnection(connectionString))\n        {\n            connection.Open();\n            SqlCeDataAdapter adapter = new SqlCeDataAdapter("SELECT * FROM MyTable", connection);\n            DataSet dataSet = new DataSet();\n            adapter.Fill(dataSet, "MyTable");\n\n            // Manipulate data in DataSet\n            DataTable table = dataSet.Tables["MyTable"];\n            foreach (DataRow row in table.Rows)\n            {\n                Console.WriteLine(row["ColumnName"]);\n            }\n\n            // Update database with changes\n            SqlCeCommandBuilder commandBuilder = new SqlCeCommandBuilder(adapter);\n            adapter.Update(dataSet, "MyTable");\n        }\n    }\n}