using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\nclass Program\n{\n    static void Main()\n    {\n        XmlDocument doc = new XmlDocument();\n        doc.Load("file.xml");\n        string idToFind = "ID1";\n        XPathNavigator navigator = doc.CreateNavigator();\n        XPathExpression expr = navigator.Compile($"//*[@id='{idToFind}']");\n        XPathNodeIterator nodes = navigator.Select(expr);\n\n        if (nodes.MoveNext())\n        {\n            Console.WriteLine("Element found: " + nodes.Current.OuterXml);\n        }\n        else\n        {\n            Console.WriteLine("Element not found.");\n        }\n    }\n}