using System;\nusing System.DirectoryServices;\n\npublic void GetAccountExpiry()\n{\n    DirectorySearcher searcher = new DirectorySearcher();\n    searcher.Filter = String.Format("(SAMAccountName={0})", "TestA33");\n    searcher.PropertiesToLoad.Add("accountExpires");\n\n    SearchResult result = searcher.FindOne();\n    if (result != null)\n    {\n        DirectoryEntry uEntry = result.GetDirectoryEntry();\n        if (uEntry.Properties["accountExpires"].Value != null)\n        {\n            long accountExpires = (long)uEntry.Properties["accountExpires"].Value;\n            \n            // Convert the accountExpires value to a DateTime object\n            DateTime expiryDate = DateTime.FromFileTime(accountExpires);\n            \n            // Display the expiry date in a readable format\n            Response.Write(expiryDate.ToString("yyyy-MM-dd HH:mm:ss"));\n        }\n        else\n        {\n            Response.Write("Account expiry date not set.");\n        }\n    }\n    else\n    {\n        Response.Write("User not found.");\n    }\n}