using System;\nusing System.Text.RegularExpressions;\nusing System.Web;\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    Uri referrer = this.Request.UrlReferrer;\n\n    if (referrer != null && \n        referrer.IsWellFormedOriginalString() &&\n        referrer.Host.Equals(this.Request.Url.Host, StringComparison.OrdinalIgnoreCase) &&\n        !ContainsMaliciousContent(referrer.ToString()))\n    {\n        // Referrer is valid and safe\n    }\n    else\n    {\n        // Handle invalid referrer\n    }\n}\n\nprivate bool ContainsMaliciousContent(string url)\n{\n    // Regular expression to detect potential XSS or other injection attacks\n    string pattern = @"<|>|%3C|%3E|script|alert|onload|onerror|javascript|data|vbscript";\n    return Regex.IsMatch(url, pattern, RegexOptions.IgnoreCase);\n}