using System;\nusing System.Security.Cryptography;\nusing System.Security.Cryptography.X509Certificates;\nusing System.Text;\n\npublic class EncryptionExample\n{\n    public static void Main()\n    {\n        // Load the certificate\n        X509Certificate2 cert = new X509Certificate2(@"c:\temp\whatever.cer");\n        RSA rsa = cert.GetRSAPublicKey();\n\n        // Generate a random 3DES key\n        TripleDESCryptoServiceProvider tripleDES = new TripleDESCryptoServiceProvider();\n        byte[] desKey = tripleDES.Key;\n        byte[] desIV = tripleDES.IV;\n\n        // Encrypt the 3DES key using the RSA public key\n        byte[] encryptedDesKey = rsa.Encrypt(desKey, RSAEncryptionPadding.Pkcs1);\n\n        // Encrypt your data with the 3DES key\n        string plainText = "Your sensitive data here";\n        byte[] plainBytes = Encoding.UTF8.GetBytes(plainText);\n        byte[] encryptedData;\n\n        using (ICryptoTransform encryptor = tripleDES.CreateEncryptor(desKey, desIV))\n        {\n            encryptedData = encryptor.TransformFinalBlock(plainBytes, 0, plainBytes.Length);\n        }\n\n        Console.WriteLine("Encrypted DES Key: " + Convert.ToBase64String(encryptedDesKey));\n        Console.WriteLine("Encrypted Data: " + Convert.ToBase64String(encryptedData));\n    }\n}