using System;\n\nnamespace ConsoleApp\n{\n    class Program\n    {\n        enum ValidValues\n        {\n            Value1,\n            Value2,\n            Value3\n        }\n\n        static void Main(string[] args)\n        {\n            Console.WriteLine("Please enter a value (Value1, Value2, Value3):");\n            string input = Console.ReadLine();\n\n            if (Enum.TryParse(input, out ValidValues parsedValue) && Enum.IsDefined(typeof(ValidValues), parsedValue))\n            {\n                Console.WriteLine("Valid input.");\n                // Proceed with the rest of your application logic\n            }\n            else\n            {\n                Console.WriteLine("Invalid input. Please enter one of the following values: Value1, Value2, Value3.");\n                // Optionally, you can prompt the user to enter the value again\n            }\n        }\n    }\n}