using System;\nusing System.Diagnostics;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string folderPath = @"C:\Path\To\Your\Folder";\n        string handlePath = @"C:\Path\To\Handle.exe";\n\n        GetLockedFiles(folderPath, handlePath);\n    }\n\n    static void GetLockedFiles(string folderPath, string handlePath)\n    {\n        ProcessStartInfo startInfo = new ProcessStartInfo\n        {\n            FileName = handlePath,\n            Arguments = folderPath,\n            RedirectStandardOutput = true,\n            UseShellExecute = false,\n            CreateNoWindow = true\n        };\n\n        using (Process process = Process.Start(startInfo))\n        {\n            using (StreamReader reader = process.StandardOutput)\n            {\n                string output = reader.ReadToEnd();\n                ParseAndDisplayLockedFiles(output);\n            }\n        }\n    }\n\n    static void ParseAndDisplayLockedFiles(string output)\n    {\n        using (StringReader reader = new StringReader(output))\n        {\n            string line;\n            while ((line = reader.ReadLine()) != null)\n            {\n                if (line.Contains(":\\"))\n                {\n                    Console.WriteLine("Locked file: " + line);\n                }\n            }\n        }\n    }\n}